     *
     * @return bool
     *
     */
    public function isBindWx(): bool
    {
        $uuid = $this->getUuid();
        $time = microtime(true) * 1000;
        $url  = "https://weixin.amazfit.com/v1/info/users.json?r={$uuid}C&t={$time}&userid={$this->user_id}&wxname=md";
        $data = $this->_post($url);
        $res  = json_decode($data['res'], true);

        return $res['data']['isbind'] == 1;
    }

