    /**
     * 获取微信绑定二维码
     *
     * @return string
     *
     */
    private function getBindQrcodeForWx(): string
    {
        $uuid = $this->getUuid();
        $time = microtime(true) * 1000;
        $url  = "https://weixin.amazfit.com/v1/bind/qrcode.json?r={$uuid}&t={$time}&userid={$this->user_id}&wxname=md";
        $data = $this->_post($url);
        $res  = json_decode($data['res'], true);

        return $res['data']['ticket'];
    }

    /**
     * 账号登录
     *
     * @return bool
     *
     */
    public function login(): bool
    {
        $url          = "https://api-user.huami.com/registrations/{$this->username}/tokens";
        $data         = [
            'phone_number' => $this->username,
            'password'     => $this->password,
            'client_id'    => 'HuaMi',
            'token'        => 'access',
            'redirect_uri' => 'https://s3-us-west-2.amazonaws.com/hm-registration/successsignin.html',
        ];
        $headers      = [
            'Content-Type: application/x-www-form-urlencoded; charset=UTF-8',
            'User-Agent: Dalvik/2.1.0 (Linux; U; Android 5.1.1; SM-G9750 Build/LMY48Z)',
        ];
        $res          = $this->_curl($url, [
            'headers' => $headers,
            'post'    => $data,
            'detail'  => true,
            'header'  => true
        ]);
        $this->access = $this->_getStrBy2Char($res['response'], '&access=', '&country');
        if ($this->access && $this->_getToken()) {

            return true;
        }

        return false;
    }

    /**
     * 令牌获取
     *
     * @return bool
     *
     */
    private function _getToken(): bool
    {
        $url     = 'https://account.huami.com/v2/client/login';
        $data    = [
            'third_name'   => 'huami_phone',
            'grant_type'   => 'access_token',
            'device_model' => 'android_phone',
            'device_id'    => '865166029072108',
            'country_code' => 'CN',
            'code'         => $this->access,
            'app_version'  => '4.6.1',
            'app_name'     => 'com.xiaomi.hm.health'
        ];
        $headers = [
            'Content-Type: application/x-www-form-urlencoded; charset=UTF-8',
            'User-Agent: Dalvik/2.1.0 (Linux; U; Android 5.1.1; SM-G9750 Build/LMY48Z)'
        ];
        $res     = $this->_curl($url, ['post' => $data, 'headers' => $headers]);
        $token   = json_decode($res, true);
        $info    = $token['token_info'];

        if (empty($info['app_token']) || empty($info['user_id'])) {

            return false;
        }

        $this->app_token = $info['app_token'];
        $this->user_id   = $info['user_id'];

        return true;
    }

    /**
     * 获取UUID
     *
     * @return string
     *
     */
    private function getUuid(): string
    {
        $chars = md5(uniqid(mt_rand(), true));
        $uuid  = substr($chars, 0, 8) . '-' . substr($chars, 8, 4) . '-' . substr($chars, 12, 4) . '-' . substr($chars,
                16, 4) . '-' . substr($chars, 20, 12);

        return strtoupper($uuid);
    }

    /**
     * @param string $url
     * @param string $data
     *
     * @return array
     *
     */
    private function _post(string $url, string $data = ''): array
    {
        $headers = [
            'Content-Type: application/x-www-form-urlencoded; charset=UTF-8',
            'apptoken: ' . $this->app_token,
        ];
        $ch      = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false);
        curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, false);
        curl_setopt($ch, CURLOPT_FOLLOWLOCATION, false);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);
        if ($data) {
            curl_setopt($ch, CURLOPT_POST, 1);
            curl_setopt($ch, CURLOPT_POSTFIELDS, $data);
        }
        $res  = curl_exec($ch);
        $code = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);

        return ['code' => $code, 'res' => $res,];
    }

    /**
     * @param string $url
     * @param array  $opt
     *
     * @return array|bool|string
     */
    private function _curl(string $url, array $opt = [])
    {
        $cookie = '';
        if (is_array($opt['cookie'])) {
            foreach ($opt['cookie'] as $k => $v) {
                $cookie .= $k . '=' . $v . '; ';
            }
        }

        $cookie = (mb_substr($cookie, 0, mb_strlen($cookie) - 2));

        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_COOKIE, $cookie);
        curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false);
        curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, false);
        curl_setopt($ch, CURLOPT_FOLLOWLOCATION, false);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_NOBODY, $opt['nobody']);
        curl_setopt($ch, CURLOPT_HEADER, $opt['header'] ?? false);
        curl_setopt($ch, CURLOPT_HTTPHEADER, $opt['headers'] ?? []);
        curl_setopt($ch, CURLOPT_TIMEOUT_MS, $opt['rtime'] ?? 10000);
        curl_setopt($ch, CURLOPT_CONNECTTIMEOUT_MS, $opt['ctime'] ?? 10000);
        if (isset($opt['post'])) {
            curl_setopt($ch, CURLOPT_POST, 1);
            curl_setopt($ch, CURLOPT_POSTFIELDS,
                is_array($opt['post']) ? http_build_query($opt['post']) : $opt['post']);
        }
        $res   = curl_exec($ch);
        $error = curl_error($ch);
        $code  = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);

        if ($opt['detail']) {
            return ['code' => $code, 'error' => $error, 'response' => $res,];
        }

        return $res;
    }

    /**
     * @param        $str
     * @param        $before
     * @param string $end
     *
     * @return string
     *
     */
    private function _getStrBy2Char($str, $before, $end = ''): string
    {
        $beginIndex = mb_strpos($str, $before);
        if ($beginIndex == false) {
            return '';
        }

        $cutStr   = mb_substr($str, $beginIndex);
        $endIndex = mb_strpos($cutStr, $end);
        if (empty($end) || $endIndex === false) {

            return $cutStr;
        }

        return mb_substr($cutStr, mb_strlen($before), $endIndex - mb_strlen($before));
    }
}
